org.jenkinsci.plugins.scripttrigger.Scri.script:
PATH=~/.rbenv/bin:~/.rbenv/shims:$PATH PREV_STEMCELL_DATE=`curl $JOB_URL/lastSuccessfulBuild/artifact/stemcell_date_added.txt` STEMCELL_BASE_URL=<%= @vars['stemcell_base_url'] %> STEMCELL_SEARCH=<%= @vars['stemcell'] %> STEMCELL_DATA=`curl "${STEMCELL_BASE_URL}/?prefix=${STEMCELL_SEARCH}" 2>/dev/null` CURR_STEMCELL_DATE=`echo $STEMCELL_DATA | sed 's/.*<LastModified>\([^<]*\)<\/LastModified>.*/\1/' | tr -d ' '`
DATE_DIFF_RB="require('time'); (Time.parse(ARGV[0]) >= Time.parse(ARGV[1])) ? Process.exit(1) : Process.exit(0)"
ruby -e "$DATE_DIFF_RB" ${PREV_STEMCELL_DATE} ${CURR_STEMCELL_DATE}
